mapper.select:
SELECT ai.id "id", alarm_time "alarmTime", abnormal_code "abnormalCode", getValue ( 'abnormal', abnormal_code ) "abnormalValue", pulverizer_point_id "pulverizerPointId", pp.point_name "pointName", detection_value "detectionValue", ifnull(pp.pulverizer_code,ai.pulverizer_code) "pulverizerCode", getValue( 'pulverizer' ,ifnull(pp.pulverizer_code,ai.pulverizer_code)) "pulverizerValue" , count "count", alarm_description "alarmDescription", verify_result "verifyResult", CASE verify_result WHEN 1 THEN '误报' WHEN 2 THEN '真实故障' END "verifyResultValue", verify_description "verifyDescription", result_status "resultStatus", CASE result_status WHEN 1 THEN '核实中' WHEN 2 THEN '已核实' WHEN 3 THEN '处理中' WHEN 4 THEN '已处理' else '未处理' END "resultStatusValue", process_description "processDescription", finish_date "finishDate", ai.position_code "positionCode", possible_cause "possibleCause", proposal "proposal", getValue ( 'position', ai.position_code ) "positionValue", -- 待补充逻辑 case when pulverizer_point_id is not null then pp.no else ( SELECT GROUP_CONCAT( p.NO ) FROM alarm_point a LEFT JOIN pulverizer_point p ON pulverizer_point_id = p.id WHERE a.pulverizer_code = ifnull(pp.pulverizer_code,ai.pulverizer_code) AND abnormal_code = alarm_code ) end nos FROM alarm_information ai LEFT JOIN pulverizer_point pp ON ai.pulverizer_point_id = pp.id WHERE 1=1
AND ${whereSql}
AND pp.pulverizer_code= #{alarmPageVO.pulverizerCode}
AND ai.verify_result= #{alarmPageVO.verifyResult}
查询报警列表失败 AND STR_TO_DATE(DATE_FORMAT(alarm_time,'%Y-%m-%d'),'%Y-%m-%d') >= STR_TO_DATE(#{alarmPageVO.startDate},'%Y-%m-%d')
AND STR_TO_DATE(DATE_FORMAT(alarm_time,'%Y-%m-%d'),'%Y-%m-%d') <= STR_TO_DATE(#{alarmPageVO.endDate},'%Y-%m-%d')
AND pp.no = #{alarmPageVO.point}
AND ai.result_status is null
AND ai.result_status = #{alarmPageVO.resultStatus}
AND ai.abnormal_code = #{alarmPageVO.abnormalCode}
AND ai.position_code = #{alarmPageVO.positionCode}
order by alarm_time
desc

select verify_result "verify_result", MONTH(alarm_time) "month", count(1) "count" from alarm_information ai LEFT JOIN pulverizer_point pp ON ai.pulverizer_point_id = pp.id
AND pp.pulverizer_code = #{pulverizerCode}
AND YEAR(alarm_time) = #{year}
AND verify_result is not null AND verify_result in (1,2)
GROUP BY verify_result,MONTH(alarm_time) ORDER BY MONTH(alarm_time)

select verify_result "verify_result", getValue('abnormal',abnormal_code) "abnormalValue", count(1) "count" from alarm_information ai LEFT JOIN pulverizer_point pp ON ai.pulverizer_point_id = pp.id
AND pp.pulverizer_code = #{pulverizerCode}
AND YEAR(alarm_time) = #{year}
AND verify_result is not null AND verify_result in (1,2)
GROUP BY verify_result,getValue('abnormal',abnormal_code)
